document.addEventListener("DOMContentLoaded",(function(){const submitButton=document.querySelector(".submit__bar"),formSection=document.querySelector(".application-form"),button=document.querySelector(".submit__button");button.addEventListener("click",e=>{e.preventDefault(),formSection.scrollIntoView({behavior:"smooth",block:"start"})});const observer=new IntersectionObserver(entries=>{entries.forEach(entry=>{entry.isIntersecting?submitButton.style.transform="translateY(100%)":submitButton.style.transform="translateY(0)"})},{threshold:.5});observer.observe(formSection);const rect=formSection.getBoundingClientRect();rect.top<window.innerHeight&&(submitButton.style.transform="translateY(100%)")}));